mapper.select:
SELECT 
`type`, count(*) AS 'count' 
FROM `Problem_Of_Student`
WHERE
	`Student_code` = #{Student_code}
	AND `YorNorX` = 'N'
	AND `subject` = #{subject}
GROUP BY `type`
ORDER BY count(*) DESC
LIMIT 5;

SELECT `Bname`, `Pchap`, `Pnumber`
FROM `Problem`
JOIN `Problem_Of_Student` ON `Problem`.`Problem_Code` = `Problem_Of_Student`.`Problem_code`
WHERE 
	`Student_code` = #{Student_code}
	AND `YorNorX` = 'N'
	AND `subject` = #{subject}
	AND `Problem`.`type` = #{type}
